import React, { useState } from "react";
import "./Expenses.css";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2024");
  // const [filterInfoText, setFilterInfoText] = useState("2022,2023 & 2025");

  let filterInfoText = "2023, 2025 & 2026";

  if (filterInfoText === "2023") {
    filterInfoText = "2024, 2025 & 2026";
  } else if (filterInfoText === "2024") {
    filterInfoText = "2023, 2025 & 2026";
  } else if (filterInfoText === "2025") {
    filterInfoText = "2023, 2024 & 2026";
  } else {
    filterInfoText = "2023, 2024 & 2025";
  }

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
    // if (selectedYear === "2022") {
    //   setFilterInfoText("2023,2024 & 2025");
    // } else if (selectedYear === "2023") {
    //   setFilterInfoText("2022,2024 & 2025");
    // } else if (selectedYear === "2024") {
    //   setFilterInfoText("2022,2023 & 2025");
    // } else {
    //   setFilterInfoText("2022,2023 & 2024");
    // }
  };

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      {props.item.map((expense, index) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
