import { useEffect, useRef, useState } from "react";
const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameValid, setEnteredNameValid] = useState(true);
  const [enteredNametouched, setEnteredNameTouched] = useState(false);

  useEffect(() => {
    if (enteredNameValid) {
      console.log("Yash is great");
    }
  }, [enteredNameValid]);
  const nameInputHandler = (e) => {
    setEnteredName(e.target.value);
  };
  const formSubmissionHandler = (e) => {
    e.preventDefault();
    if (enteredName.trim() === "") {
      setEnteredNameValid(false);
      return;
    }

    setEnteredNameValid(true);
    console.log(enteredName);
    const enteredValue = nameInputRef.current.value;
    console.log(enteredValue);
    setEnteredName("");
  };
  const nameInputValid = !enteredNameValid && enteredNametouched;
  const nameInputClasses = enteredNameValid
    ? "form-control"
    : "form-control invalid";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          type="text"
          id="name"
          onChange={nameInputHandler}
          value={enteredName}
        />
        {!enteredNameValid && (
          <p className="error-text">Please Enter the values in the inputs</p>
        )}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
